jQuery(window).load(function(){
	/* Isotope Sorting */
	jQuery(function(){
	  var $container = $('.portfolio_block');
	  
	  $('.posts_block').isotope({
		itemSelector : '.element'
	  });
	  
	  $container.isotope({
		itemSelector : '.element'
	  });
		
	  var $optionSets = $('#options .optionset'),
		  $optionLinks = $optionSets.find('a');
	
	  $optionLinks.click(function(){
		var $this = $(this);
		// don't proceed if already selected
		if ( $this.parent('li').hasClass('selected') ) {
		  return false;
		}
		var $optionSet = $this.parents('.optionset');
		$optionSet.find('.selected').removeClass('selected');
		$optionSet.find('.fltr_before').removeClass('fltr_before');
		$optionSet.find('.fltr_after').removeClass('fltr_after');
		$this.parent('li').addClass('selected');
		$this.parent('li').next('li').addClass('fltr_after');
		$this.parent('li').prev('li').addClass('fltr_before');
	
		// make option object dynamically, i.e. { filter: '.my-filter-class' }
		var options = {},
			key = $optionSet.attr('data-option-key'),
			value = $this.attr('data-option-value');
		// parse 'false' as false boolean
		value = value === 'false' ? false : value;
		options[ key ] = value;
		if ( key === 'layoutMode' && typeof changeLayoutMode === 'function' ) {
		  // changes in layout modes need extra logic
		  changeLayoutMode( $this, options )
		} else {
		  // otherwise, apply new options
		  $container.isotope(options);	  
		}	
		return false;	
	  });
		$('.masonry').find('img').load(function(){
			$container.isotope('reLayout');
		}); 
				
	});
	
	jQuery.fn.portfolio_addon = function(addon_options) {
		//Set Variables
		var addon_el = jQuery(this),
			addon_base = this,
			img_count = addon_options.items.length,
			img_per_load = addon_options.load_count,
			$newEls = '',
			loaded_object = '',
			$container = jQuery('.image-grid');
		
		jQuery('.btn_load_more').click(function(){
			$newEls = '';
			loaded_object = '';									   
			loaded_images = $container.find('.added').size();
			if ((img_count - loaded_images) > img_per_load) {
				now_load = img_per_load;
			} else {
				now_load = img_count - loaded_images;
			}
			
			if ((loaded_images + now_load) == img_count) jQuery(this).parents('.load_more_cont').fadeOut();
	
			if (loaded_images < 1) {
				i_start = 1;
			} else {
				i_start = loaded_images+1;
			}
	
			if (now_load > 0) {
				if (addon_options.type == 0) {
					/*//1 Column Service Type
					for (i = i_start-1; i < i_start+now_load-1; i++) {
						loaded_object = loaded_object + '<div data-category="'+ addon_options.items[i].category +'" class="'+ addon_options.items[i].category +' element row-fluid added"><div class="filter_img span6"><div class="wrapped_img"><a href="'+ addon_options.items[i].post_zoom +'" class="prettyPhoto" rel="prettyPhoto[portfolio1]"><img src="'+ addon_options.items[i].src +'" alt="" width="570" height="340"></a></div></div><div class="portfolio_dscr span6"><div class="bg_title"><h4><a href="'+ addon_options.items[i].url +'">'+ addon_options.items[i].title +'</a></h4></div>'+ addon_options.items[i].description +'</div></div>';
					}*/
				} else {
					//2-4 Columns Service Type
					for (i = i_start-1; i < i_start+now_load-1; i++) {
						loaded_object = loaded_object + '<div class="'+ addon_options.items[i].sortcategory +' element col-md-4 added"><div class="portfolio_item_wrapper"><div class="img_block hover_img"><a href="'+ addon_options.items[i].url +'"><img src="'+ addon_options.items[i].src +'" alt="" /><div class="item_fadder"></div><div class="link_ico"><i class="fa fa-link"></i></div></a></div><h3 class="gallery_title"><a href="'+ addon_options.items[i].url +'">'+ addon_options.items[i].title +'</a></h3></div></div>';
	
					}
				}				
				
				$newEls = jQuery(loaded_object);
				$container.isotope('insert', $newEls, function() {
					$container.isotope('reLayout');
					
					jQuery('.prettyPhoto').prettyPhoto();												
				});			
			}
		});
	}
});

